#!/bin/sh

source=./dist/vendo/vendo.dto.d.ts
target=../client/src/lib/vendo.dto.ts

echo "/* \n\
 * Autogenerated by backend build - Do not edit here! \n\
 * Run \"npm run build\" from the server directory to update this file. \n\
 */\n" > $target

cat $source| grep -v -e "import" -e "constructor" >> $target

sed -E -i 's/declare class (.*) /type \1 = /g' $target
